import { StyleSheet, View, Text, TextInput, Button } from "react-native";

const RegistrationScreen = () => {
  return (
    <View style={stylesRegister.form}>
      <View style={stylesRegister.image}></View>
      <TouchableOpacity>
        <PlusPhoto style={stylesRegister.icon} />
      </TouchableOpacity>
      <Text style={stylesRegister.title}>Реєстрація</Text>
      <TextInput style={stylesRegister.input} placeholder="Логін" />
      <TextInput
        style={stylesRegister.input}
        placeholder="Адреса електронної пошти"
      />
      <View>
        <TextInput
          style={stylesRegister.lastInput}
          placeholder="Пароль"
        ></TextInput>
        <TouchableOpacity style={stylesRegister.showPassword}>
          <Text style={stylesRegister.textShow}>Показати</Text>
        </TouchableOpacity>
      </View>
      <TouchableOpacity style={stylesRegister.buttonForm}>
        <Text style={stylesRegister.textButton}>Зареєстуватися</Text>
      </TouchableOpacity>
      <Text style={stylesRegister.textLink}>Вже є акаунт? Увійти</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  box: {
    backgroundColor: "#FFFFFF",
    // bottom: "20%",
    // marginTop: 500,
    // paddingBottom: "auto",
  },
  title: {
    color: "#212121",
    fontSize: 30,
    // font: Roboto,
    // Weight: 500,
    // Size: 30,
    // LineHeight: 35.16,
    // Letter: 1 %,
    // Align: Center,
  },
  inputs: {
    display: "flex",
    // gap: '16px'
  },
  login: {
    backgroundColor: "#F6F6F6",
    borderWidth: 1,
    borderColor: "#BDBDBD",

    color: "#BDBDBD",
  },
  email: {
    backgroundColor: "#F6F6F6",
    borderWidth: 1,
    borderColor: "#BDBDBD",

    color: "#BDBDBD",
  },
  password: {
    backgroundColor: "#F6F6F6",
    borderWidth: 1,
    borderColor: "#BDBDBD",
    
    color: "#BDBDBD",
  },
});

export default RegistrationScreen;
