import { StyleSheet, View, Text, TextInput, Button, TouchableOpacity } from "react-native";

const RegistrationScreen = () => {
  return (
    <View style={styles.form}>
      <View style={styles.image}></View>
      {/* <TouchableOpacity>
        <PlusPhoto style={styles.icon} />
      </TouchableOpacity> */}
      <Text style={styles.title}>Реєстрація</Text>
      <TextInput style={styles.input} placeholder="Логін" />
      <TextInput
        style={styles.input}
        placeholder="Адреса електронної пошти"
      />
      <View>
        <TextInput
          style={[styles.input, styles.passwordInput]}
          placeholder="Пароль"
        ></TextInput>
        <TouchableOpacity style={styles.showPasswordLink}>
          <Text style={styles.textShow}>Показати</Text>
        </TouchableOpacity>
      </View>
      <TouchableOpacity style={styles.buttonForm}>
        <Text style={styles.textButton}>Зареєстуватися</Text>
      </TouchableOpacity>
      <Text style={styles.textLink}>Вже є акаунт? Увійти</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  form: {
    position: "relative",
    paddingBottom: 80,
    paddingTop: 90,
    paddingRight: 16,
    paddingLeft: 16,
    width: "100%",
    backgroundColor: "#FFFFFF",
    borderTopLeftRadius: 25,
    borderTopRightRadius: 25,
    textAlign: "center",
  },
  image: {
    // width: 120,
    // height: 120,
    // position: "absolute",
    // backgroundColor: "#F6F6F6",
    // borderRadius: 16,
    // left: "39%",
    // top: "-15%",
  },
  title: {
    textAlign: "center",
    fontSize: 30,
    lineHeight: 35,
    marginBottom: 30,
    fontFamily: "RobotoMedium",
  },
  input: {
    backgroundColor: "#F6F6F6",
    borderRadius: 8,
    paddingTop: 16,
    paddingLeft: 16,
    paddingBottom: 15,
    marginBottom: 16,
  },
  passwordInput: {
    marginBottom: 43,
    position: "relative",
  },
  showPasswordLink: {
    position: "absolute",
    top: 20,
    left: "75%",
  },
  textShow: {
    color: "#1B4371",
    fontFamily: "Roboto",
    fontSize: 16,
    lineHeight: 19,
  },
  buttonForm: {
  //   backgroundColor: "#FF6C00",
  //   color: "black",
  //   marginBottom: 16,
  //   paddingTop: 16,
  //   paddingBottom: 16,
  //   borderRadius: 100,
  },
  textButton: {
  //   color: "#FFFFFF",
  //   textAlign: "center",
  //   fontFamily: "Roboto",
  },
  textLink: {
  //   fontFamily: "Roboto",
  //   fontSize: 16,
  //   textAlign: "center",
  //   color: "#1B4371",
  },
});

export default RegistrationScreen;
