import { StyleSheet, View, Text, TextInput, Button } from "react-native";

const RegistrationScreen = () => {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Реєстрація</Text>
      <TextInput style={styles.login} placeholder="Логін" />
          <TextInput style={styles.email} placeholder="Адреса електронної пошти" />
          <TextInput style={styles.password} placeholder="пароль" />
      <Button title="Зареєструватися"/>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#FFFFFF",
    // marginTop: 500,
    paddingBottom: "auto",
  },
  title: {
    color: "#212121",
    fontSize: 30,
    // font: Roboto,
    // Weight: 500,
    // Size: 30,
    // LineHeight: 35.16,
    // Letter: 1 %,
    // Align: Center,
  },
  login: {
    backgroundColor: "#F6F6F6",
    border: "1px solid #BDBDBD",
    color: "#BDBDBD",
  },
  email: {
    backgroundColor: "#F6F6F6",
    border: "1px solid #BDBDBD",
    color: "#BDBDBD",
  },
  password: {
    backgroundColor: "#F6F6F6",
    border: "1px solid #BDBDBD",
    color: "#BDBDBD",
  },
});

export default RegistrationScreen;
